import { Component, OnInit } from '@angular/core';
import { FormBuilder, Validators, FormGroup } from '@angular/forms';
import { Labor } from 'src/app/interfaces/labor';

@Component({
  selector: 'app-add-edit-labor',
  templateUrl: './add-edit-labor.component.html',
  styleUrls: ['./add-edit-labor.component.css']
})
export class AddEditLaborComponent implements OnInit{
  formLabor: FormGroup;
  constructor(private fb: FormBuilder){
    this.formLabor = this.fb.group({
      tipLabId: [null, Validators.required],
      nombreLab: ['', Validators.required],
      horasLab: [null, Validators.required]
    })
  }

  ngOnInit(): void {

  }
  addLabor(){
    console.log(this.formLabor);
    /*
    const labor: Labor = {
      name
    }*/
  }
}
<div class="row mb-3 mt-10">
    <div class="col-lg4">
        <div class="card bg-secondary mb-3" style="max-width: 20rem;">
            <div class="card-header text-center">Agregar Labor</div>
            <div class="card-body">
                <form [formGroup]="formLabor" (ngSubmit)="addLabor()">
                    <input formControlName="tipLabId" type="number" class="form-control" placeholder="Ingrese ID Tipo Labor">
                    <span *ngIf="formLabor.get('tipLabId')?.hasError('requiered')">
                        El campo ID Tipo Labor es <strong>requerido</strong>
                    </span>
                    <input formControlName="nombreLab" type="text" class="form-control" placeholder="Ingrese Nombre Labor">
                    <input formControlName="horasLab" type="number" class="form-control" placeholder="Ingrese Cantidad de Horas">
                    <div class="row mt-3 text-center">
                        <div class="col">
                            <button [disabled]="formLabor.invalid" type="submit" class="btn btn-secondary">Aceptar</button>
                        </div>
                        <div class="col">
                            <button routerLink="/labor" type="button" class="btn btn-secondary">Cancelar</button>
                        </div>
                    </div>
                </form>
            </div>
        </div>
    </div>
</div>
