import { Component, OnInit } from '@angular/core';
import { Labor } from 'src/app/interfaces/labor';
import { LaborService } from 'src/app/services/labor.service';

@Component({
  selector: 'app-list-labor',
  templateUrl: './list-labor.component.html',
  styleUrls: ['./list-labor.component.css']
})
export class ListLaborComponent implements OnInit {
  listLabores: Labor[] = []
  constructor(private _laborService: LaborService){}

  ngOnInit(): void {
    this.getListLabores();
  }

  getListLabores(){
    this._laborService.getListLabores().subscribe((data: Labor[]) =>{
      this.listLabores = this.convertirLabores(data);
    })
  }

  deleteLabor(id: number){
    this._laborService.deleteLabor(id).subscribe(() =>{
      this.getListLabores();
    })
  }

  convertirLabores(labores: any): Labor[]{
    let resultado: Labor[] =[];
    for(let labor of labores){
      resultado.push({
        id: labor.atrLabID,
        idTipoLabor: labor.atrTlID,
        nombre: labor.atrLabNombre,
        horas: labor.atrLabHoras
      });
    }
    return resultado;
  }

  convertirLabor(labor: any): Labor{
    let resultado: Labor =({
      id: labor.atrLabID,
      idTipoLabor: labor.atrTlID,
      nombre: labor.atrLabNombre,
      horas: labor.atrLabHoras
    });
    return resultado;
  }
}

<app-navbar></app-navbar>
<div class="container">
    <nav class="navbar navbar-expand-lg bg-primary" data-bs-theme="dark">
        <div class="container-fluid">
          <a class="navbar-brand" href="#">Gestión de Autoevaluaciones Universidad del Cauca</a>
          <img src="https://upload.wikimedia.org/wikipedia/commons/thumb/5/57/Logo_de_la_Universidad_del_Cauca.svg/80px-Logo_de_la_Universidad_del_Cauca.svg.png" alt="Universidad del Cauca" class="university-logo">
        </div>
    </nav>

    <div class="infoUser">
      <i class="fa-regular fa-user"></i>
    </div>

    <div class="title">
        <h4>Lista Labores</h4>
        <button type="button" class="btn btn-secondary" routerLink="/labor/add">Agregar Labor</button>
    </div>

    <table class="table table-hover">
        <thead>
        <tr>
            <th scope="col">Id</th>
            <th scope="col">Tipo Labor</th>
            <th scope="col">Nombre</th>
            <th scope="col">Cantidad Horas</th>
            <th scope=""></th>
        </tr>
        </thead>
        <tbody>
        <tr *ngFor=" let labor of listLabores" class="table-active">
            <td>{{labor.id}}</td>
            <td>{{labor.idTipoLabor}}</td>
            <td>{{labor.nombre}}</td>
            <td>{{labor.horas}}</td>
            <td>
                <button routerLink="/labor/edit/{{labor.id}}" type="button" class="btn btn-primary btn-sm ms-2">Editar</button>
                <button (click)="deleteLabor(labor.id!)" type="button" class="btn btn-danger btn-sm">Eliminar</button>
            </td>
        </tr>
        </tbody>
    </table>
</div>
