import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable, catchError, throwError } from 'rxjs';
import { environment } from 'src/environments/environment';

@Injectable({
  providedIn: 'root'
})
export class ItemService {

  private myAppUrl: string;
  private myApiUrl: string;

  constructor(private http: HttpClient) { 
    this.myAppUrl = environment.apiUrl;
    this.myApiUrl = '/items/';
  }

  saveItem(idUser: number, idLabor: number): Observable <void>{
    const token = localStorage.getItem('token');
    if(!token){
      return throwError('Token not provided!');
    }else{
      const headers = new HttpHeaders({ Authorization: token });
      console.log(`${this.myAppUrl}${this.myApiUrl}addItem/1`);
      this.http.get<void>(`${this.myAppUrl}${this.myApiUrl}addItem/1`);
      return this.http.get<void>(`${this.myAppUrl}${this.myApiUrl}addItem/1`);
    }
  }
}
